    global global_bot
    global_bot = bot
    dp.register_message_handler(lessons, Text(equals="–ö—É–ø–∏—Ç—å –ø–æ–ª–Ω—ã–π –∫—É—Ä—Å"))
    dp.register_message_handler(lessons, Text(startswith="–£—Ä–æ–∫"))
    dp.register_message_handler(cmd_start)

